mapper.select:
/* EslInterfaceMapper.selectVTetpluEsl */ SELECT * FROM ( SELECT STORE_CODE , PLU_CODE , PRICE_SECT , START_DATE , END_DATE , MD_CODE , PUM_CODE , EVENT_SECT , CLASS_CODE , TO_CHAR(RAWTOHEX(PLU_NAME)) AS PLU_NAME , CURR_SAL_PRICE , GOODS_SECT , MG_RATE , TAX_FLAG , SYS_DATE , SYS_TIME , TO_CHAR(RAWTOHEX(ORIGIN_NAME)) AS ORIGIN_NAME , NOR_SAL_PRICE , CONTENTS_QTY , TO_CHAR(RAWTOHEX(DISPLAY_UNIT_NAME)) AS DISPLAY_UNIT_NAME , DISPLAY_UNIT_QTY , DEAL_GUBN , ORIGIN_CODE , TO_CHAR(RAWTOHEX(BRAND_NAME)) AS BRAND_NAME , CATE_CODE , TO_CHAR(RAWTOHEX(CATE_NAME)) AS CATE_NAME , MOD_DATE , MOD_TIME , MOD_EMPNO , IF_DATE , IF_TIME , IF_EMPNO , DISPLAY_UNIT_PRICE , TO_CHAR(RAWTOHEX(SELLNG_PNT)) AS SELLNG_PNT , GOOS_AUTH_IMG , WINE_SUGR_CONT , WINE_BODY , WINE_KIND , TO_CHAR(RAWTOHEX(WINE_EVAL_1)) AS WINE_EVAL_1 , TO_CHAR(RAWTOHEX(WINE_EVAL_2)) AS WINE_EVAL_2 , TO_CHAR(RAWTOHEX(WINE_EVAL_3)) AS WINE_EVAL_3 , TO_CHAR(RAWTOHEX(WINE_PLOR)) AS WINE_PLOR , TO_CHAR(RAWTOHEX(WINE_ITEM_KIND)) AS WINE_ITEM_KIND , TO_CHAR(RAWTOHEX(WINE_VINT)) AS WINE_VINT , MOBL_CUP_GOOS_YN , LAYOUT_GUBN_1 , LAYOUT_GUBN_2 , '' AS PLOR_ORIGIN_NAME , '' AS QR_CODE , CONCAT(CONTENTS_QTY, DISPLAY_UNIT_PRICE) AS CONTENTS_QTY_UNIT
, CASE WHEN CONTENTS_QTY IS NOT NULL AND CONTENTS_QTY > 0 THEN CASE WHEN TRIM(DISPLAY_UNIT_NAME) = 'ml' THEN ROUND((CURR_SAL_PRICE/VTRE.CONTENTS_QTY) * 100) WHEN TRIM(DISPLAY_UNIT_NAME) = 'L' THEN ROUND((CURR_SAL_PRICE/VTRE.CONTENTS_QTY) * 1000 * 100) ELSE NULL END ELSE NULL END AS COMBINE_DISPLAY_UNIT_PRICE
, VTRE.GOOS_INFO_IMG , ROW_NUMBER() OVER(ORDER BY PLU_CODE ASC) AS rnum FROM IF_ESL_DPT0.V_TETPLU_ESL VTRE WHERE STORE_CODE = '14' ) WHERE rnum >= ${startNum} and rnum <= ${endNum}

/* EslInterfaceMapper.selectVTetpluEsl */ SELECT * FROM ( SELECT STORE_CODE , PLU_CODE , PRICE_SECT , START_DATE , END_DATE , MD_CODE , PUM_CODE , EVENT_SECT , CLASS_CODE , TO_CHAR(RAWTOHEX(PLU_NAME)) AS PLU_NAME , CURR_SAL_PRICE , GOODS_SECT , MG_RATE , TAX_FLAG , SYS_DATE , SYS_TIME , TO_CHAR(RAWTOHEX(ORIGIN_NAME)) AS ORIGIN_NAME , NOR_SAL_PRICE , CONTENTS_QTY , TO_CHAR(RAWTOHEX(DISPLAY_UNIT_NAME)) AS DISPLAY_UNIT_NAME , DISPLAY_UNIT_QTY , DEAL_GUBN , ORIGIN_CODE , TO_CHAR(RAWTOHEX(BRAND_NAME)) AS BRAND_NAME , CATE_CODE , TO_CHAR(RAWTOHEX(CATE_NAME)) AS CATE_NAME , MOD_DATE , MOD_TIME , MOD_EMPNO , IF_DATE , IF_TIME , IF_EMPNO , DISPLAY_UNIT_PRICE , TO_CHAR(RAWTOHEX(SELLNG_PNT)) AS SELLNG_PNT , GOOS_AUTH_IMG , WINE_SUGR_CONT , WINE_BODY , WINE_KIND , TO_CHAR(RAWTOHEX(WINE_EVAL_1)) AS WINE_EVAL_1 , TO_CHAR(RAWTOHEX(WINE_EVAL_2)) AS WINE_EVAL_2 , TO_CHAR(RAWTOHEX(WINE_EVAL_3)) AS WINE_EVAL_3 , TO_CHAR(RAWTOHEX(WINE_PLOR)) AS WINE_PLOR , TO_CHAR(RAWTOHEX(WINE_ITEM_KIND)) AS WINE_ITEM_KIND , TO_CHAR(RAWTOHEX(WINE_VINT)) AS WINE_VINT , MOBL_CUP_GOOS_YN , LAYOUT_GUBN_1 , LAYOUT_GUBN_2 , '' AS PLOR_ORIGIN_NAME , '' AS QR_CODE , CONCAT(CONTENTS_QTY, DISPLAY_UNIT_PRICE) AS CONTENTS_QTY_UNIT
, CASE WHEN CONTENTS_QTY IS NOT NULL AND CONTENTS_QTY > 0 THEN CASE WHEN TRIM(DISPLAY_UNIT_NAME) = 'ml' THEN ROUND((CURR_SAL_PRICE/VTRE.CONTENTS_QTY) * 100) WHEN TRIM(DISPLAY_UNIT_NAME) = 'L' THEN ROUND((CURR_SAL_PRICE/VTRE.CONTENTS_QTY) * 1000 * 100) ELSE NULL END ELSE NULL END AS COMBINE_DISPLAY_UNIT_PRICE
, VTRE.GOOS_INFO_IMG , ROW_NUMBER() OVER(ORDER BY PLU_CODE ASC) AS rnum FROM IF_ESL_DPT0.V_TETPLU_ESL VTRE WHERE STORE_CODE = '14' AND MOD_DATE = TO_CHAR(SYSDATE, 'yyyymmdd') ) WHERE rnum >= ${startNum} and rnum <= ${endNum}
